// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "./Mytoken.sol";
import "./MyNFT.sol";

contract NFTFarm {
    string public name = "Icey Pandaz Token Farm";
    uint256 public TOKEN_PER_DAY = 10;

    address public owner;
    MyToken public myToken;
    MyNFT public myNFT;

    mapping(address => bool) public hasStaked;
    mapping(address => bool) public isStaking;
    mapping(address => uint256) public totalStaked;
    mapping(address => uint256) public lastRewarded;
    mapping(address => mapping(uint256 => bool)) public stakingRecord;

    event NewStaker(address staker, uint256 id);
    event UnStaked(address staker, uint256 id);
    event rewardsClaimed(address user, uint256 total);

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    constructor(MyToken _myToken, MyNFT _myNFT) {
        myToken = _myToken;
        myNFT = _myNFT;
        owner = msg.sender;
    }

    function stake(uint256[] calldata _id) public {
        for (uint256 index = 0; index < _id.length; index++) {
            myNFT.transferFrom(msg.sender, address(this), _id[index]);
            //reset rewarded time
            lastRewarded[msg.sender] = block.timestamp;
            // Add user to stakers map
            stakingRecord[msg.sender][_id[index]] = true;
            //update balance
            totalStaked[msg.sender] += 1;
            // Update staking status
            isStaking[msg.sender] = true;
            hasStaked[msg.sender] = true;
            emit NewStaker(msg.sender, _id[index]);
        }
    }

    function unstake(uint256 _id) public {
        require(totalStaked[msg.sender] > 0, "Not a staker");
        require(stakingRecord[msg.sender][_id], "Not staking specific item");
        lastRewarded[msg.sender] = block.timestamp;
        myNFT.transferFrom(address(this), msg.sender, _id);
        stakingRecord[msg.sender][_id] = false;
        totalStaked[msg.sender] -= 1;
        if (totalStaked[msg.sender] <= 0) {
            isStaking[msg.sender] = false;
        }
        emit UnStaked(msg.sender, _id);
    }

    function claimReWards() public {
        require(totalStaked[msg.sender] > 0, "Not a staker");
        uint256 available = lastRewarded[msg.sender] + 1 days;
        require(available <= block.timestamp, "Rewards available after 24hrs");
        uint256 diff = block.timestamp - lastRewarded[msg.sender];
        uint256 totalRewards = (diff / 1 days) *
            totalStaked[msg.sender] *
            TOKEN_PER_DAY;
        lastRewarded[msg.sender] = block.timestamp;
        myToken.mint(msg.sender, totalRewards);
        emit rewardsClaimed(msg.sender, totalRewards);
    }

    function setTokenPerDay(uint256 _state) public onlyOwner {
        TOKEN_PER_DAY = _state;
    }
}
